import React, { Component } from 'react'

export default class Fcomponent extends Component {
 constructor(props) {
   super(props)

   this.state = {
      error: null,
      isLoaded: false,
      items: []
   }
 }

 componentDidMount(){
     debugger
     fetch('https://jsonplaceholder.typicode.com/posts')
     .then(res=> res.json())
     .then(
         (result) => {
            this.setState({
                isLoaded: true,
                items: result.name
            })
         },
         (error) => {
             this.setState({
                 isLoaded: true,
                 error
             })
         }
     )
 }

    render() {
        const {error,  isLoaded, items} =this.state;
        console.log(items)
    if(error){
return <p>error {error.message}</p>
    }else if(!isLoaded){
        return <p>Loading...</p>
    }else{
        return (
           <div>
               {items.title}
           </div>
        )
    }

  }
}
